$("#add-photo").on("change",function(){if("off"==$(this).val()){$(".photo-actived").hide();var t={photo_active:!1,promo_id:$(this).attr("data-promo")};$.post("/admin/promos-activate-photo",t)}else{$(".photo-actived").show();var t={photo_active:!0,promo_id:$(this).attr("data-promo")};$.post("/admin/promos-activate-photo",t)}}),$(".choose_from_album").click(function(){$(".choose_from_album").removeClass("photo-selected"),$(this).addClass("photo-selected"),$("#thumb-selected").html('<img src="'+$(this).attr("src")+'" alt="" class="center-thumb thumb-selected" id="'+$(this).attr("id")+'"/>');var t=$("#thumb-selected img").attr("id");$("#photo-selected-to-register").val(t)});